import * as api from "../api";
export const getPosts = () => async (dispatch) => {
  api
    .fetchPosts()
    .then((result) => result.json())
    .then((posts) => {
      dispatch({
        type: "FETCH_ALL",
        payload: posts,
      });
    })
    .catch((err) => console.log(err.message));
  // try {

  //   const { data } = await api.fetchPosts();
  //   dispatch({ type: "FETCH_ALL", payload: data });
  // } catch (error) {
